$path = "C:\Users\Default\Desktop\Check-Recall.bat"

# Contenido del archivo .bat con comprobación de admin
$batContent = @"
@echo off
:: Comprobar si es administrador
net session >nul 2>&1
if %errorLevel% NEQ 0 (
    echo ==============================
    echo Este script requiere privilegios de Administrador.
    echo Reintentando con permisos elevados...
    echo ==============================
    powershell -Command "Start-Process '%~f0' -Verb runAs"
    exit /b
)

echo ==============================
echo Comprobando estado de Recall...
echo ==============================
Dism /Online /Get-FeatureInfo /FeatureName:Recall > "%TEMP%\recall_status.txt" 2>&1

findstr /C:"Error: 0x800f080c" "%TEMP%\recall_status.txt" >nul
if %errorlevel%==0 (
    echo Recall NO EXISTE en este sistema.
    goto :end
)

findstr /C:"State : Enabled" /C:"Estado : Habilitado" "%TEMP%\recall_status.txt" >nul
if %errorlevel%==0 (
    echo Recall esta ACTIVADO.
    echo ==============================
    echo Desactivando Recall...
    echo ==============================
    Dism /Online /Disable-Feature /FeatureName:Recall
    goto :end
)
findstr /C:"State : Disabled" /C:"Estado : Deshabilitado" "%TEMP%\recall_status.txt" >nul
if %errorlevel%==0 (
    echo Recall esta DESACTIVADO.
    goto :end
)

:end
echo ==============================
echo Proceso finalizado.
pause
"@

# Crear archivo .bat en el escritorio por defecto
New-Item -Path $path -ItemType File -Force
$batContent | Out-File -FilePath $path -Encoding OEM -Force

